import React from 'react';
import styled from "styled-components";


const ContainerCard = styled.div`
    position: absolute;
    width: 300px;
    height: 250px;
    top: 35vh;
    right: 50vw;
    :nth-of-type(2) {
        top: 35vh;
        left: 50vw;
    }
    text-align: bottom;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 500px;
    color: #1de8b5;
    :hover {
        box-shadow: 0 0 70px#00b585;
        color:#00b585;
    }
    :active {
        box-shadow: 0 0px 70px rgba(0, 0, 0, 0.8);
        color:#00b585;
    }
`  

const BackgroundImg = styled.img`
    opacity: 0.5;
    width: 100%;
    height: 100%;
    border-radius: 500px;
`

const Title = styled.h3`
    position: absolute;
    bottom: 22%;
    font-size: 40pt;
    z-index: 9;
`

function RedirectCard(props) {
    return (
        <ContainerCard onClick={props.onClick}>
            <Title>{props.title}</Title>
            <BackgroundImg src={props.img}/>
        </ContainerCard>
    );
}


export default RedirectCard;
