import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import styled from "styled-components";
import { getTrips } from "../../actions"

const ListTripsWrapper = styled.form`
  width: 100%;
  height: 100vh;
  gap: 10px;
  place-content: center;
  justify-items: center;
  display: grid;
`;

class ListTripsPage extends Component {

  componentDidMount(){

  }




  handleFieldChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    return (
      <ListTripsWrapper>
          <h1>Lista de viagens disponíveis:</h1>
          <div>
          {this.props.getTrips.map(task => {
                    return (
                        <li key={task.id}>
                            <input onClick={() => this.props.toggleTask(task.id)} type="checkbox" checked={task.done} />
                            {task.text}
                            <span 
                                style={{ color: 'red' }}
                                onClick={() => this.props.deleteTask(task.id)}
                            >
                                X
                            </span>
                        </li>
                    )
                })}
          </div>


      </ListTripsWrapper>
    );
  }
}

const mapStateToProps = (state) => {
  return {
      allTasks: state.tasks.allTasks
  }
}


function mapDispatchToProps(dispatch){
  return{
    getTrips: () => dispatch(),
    goToListTripsPage: () => dispatch(push('/')),
    goToTripDetailsPage: () => dispatch(push('/trips/details')),
  }
}

export default connect(mapStatetoProps, mapDispatchToProps)(ListTripsPage);
