import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import { routes } from "../Router";
import { Header, Logo, Banner, MainContainer } from "../../style/global";
import LogoTipo from "../../resources/planeta.png";
import BannerImg from "../../resources/banner2.webp";
import RedirectCard from "../../components/RedirectCard";
import Astronauta from "../../resources/astronauta.png";
import Nave from "../../resources/lancamento.png";

const Title = styled.h3`
    color: white;
    position: absolute;
    bottom: 0;
    font-size: 40pt;
    z-index: 9;
`


class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }

  render() {
    return (
      <MainContainer>
          <Header>
            <Logo src={LogoTipo}  onClick={this.props.goToHomePage}/>
            <h1>FutureX</h1>
          </Header>
          <Banner src={BannerImg}/>
          <RedirectCard onClick={this.props.goToLoginPage} img={Nave}/>
          <Title></Title>
          <RedirectCard onClick={this.props.goToApplicationPage}  img={Astronauta}/>
      </MainContainer>
    );
  }
}



const mapDispatchToProps = dispatch => ({
  goToHomePage: () => dispatch(push(routes.root)),
  goToLoginPage: () => dispatch(push(routes.login)),
  goToApplicationPage: () => dispatch(push(routes.application))
})



export default connect(null, mapDispatchToProps)(HomePage);
