import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import styled from "styled-components";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';



const ApplicationFormWrapper = styled.form`
  width: 100%;
  height: 100vh;
  gap: 10px;
  place-content: center;
  justify-items: center;
  display: grid;
`;



class ApplicationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      age: "",
      applicationText: "",
      profession: "",
      country: "",
      trip:""
    };
  }

  handleFieldChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    const { name, age, applicationText, profession, country, trip } = this.state;


    return (
      <ApplicationFormWrapper>
        <TextField
          onChange={this.handleFieldChange}
          name="name"
          type="text"
          label="name"
          value={{name} || ""}
          required
          inputProps={{ pattern: "[A-Za-z áÁ_e]{3,}"}}          
        />

        <TextField
          onChange={this.handleFieldChange}
          name="age"
          type="number"
          value={{age} || ""}
          required
          inputProps={{ min:"18"}}          
        />
        <TextField
          onChange={this.handleFieldChange}
          name="applicationText"
          type="text"
          label="applicationText"
          value={{applicationText} || ""}
          required
          inputProps={{ pattern: "[A-Za-z \dáÁ_e]{30,}"}}         
        />
        <TextField
          onChange={this.handleFieldChange}
          name="profession"
          type="text"
          label="profession"
          value={{profession} || ""}
          required
          inputProps={{ pattern: "[A-Za-z áÁ]{10,}" }}          
        />
         {/* <select
          value={this.state.planet}
          onChange={this. handleFieldChange}
          input={<Input name="planet" id="planet" />}
        >
          <option value="">Planeta</option>
          <option value="mercurio">Mercúrio</option>
          <option value="venus">Vênus</option>
          <option value="terra">Terra</option>
          <option value="marte">Marte</option>
          <option value="jupiter">Júpiter</option>
          <option value="saturno">Saturno</option>
          <option value="urano">Urano</option>
          <ption value="netuno">Neturno</ption>
        </select> */}
        <Button>Aplicar</Button>
      </ApplicationFormWrapper>
    );
  }
}

export default ApplicationForm;
