import React, { Component } from "react";
import { connect } from "react-redux";
import { push } from "connected-react-router";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import styled from "styled-components";
import Input from '@material-ui/core/Input';


const ApplicationFormWrapper = styled.form`
  width: 100%;
  height: 100vh;
  gap: 10px;
  place-content: center;
  justify-items: center;
  display: grid;
`;



class ApplicationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      age: "",
      applicationText: "",

    };
  }

  handleFieldChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    const { name, planet, date, description, duration } = this.state;


    return (
      <ApplicationFormWrapper>
        <TextField
          onChange={this.handleFieldChange}
          name="name"
          type="text"
          label="name"
          value={this.state.name || ""}
          required
          inputProps={{ pattern: "[A-Za-z \dáÁ_e]{5,100}"}}          
        />

        <select
          value={this.state.planet}
          onChange={this. handleFieldChange}
          input={<Input name="planet" id="planet" />}
        >
          <option value="">Planeta</option>
          <option value="mercurio">Mercúrio</option>
          <option value="venus">Vênus</option>
          <option value="terra">Terra</option>
          <option value="marte">Marte</option>
          <option value="jupiter">Júpiter</option>
          <option value="saturno">Saturno</option>
          <option value="urano">Urano</option>
          <ption value="netuno">Neturno</ption>
        </select>

        <TextField
          onChange={this.handleFieldChange}
          name="date"
          type="date"
          value={this.state.date || ""}
          required
          inputProps={{ pattern: "(0[1-9]|[12][0-9]|3[01])/(0[1-9]|1[12])/(20/20)\d{4}"}}          
        />
        <TextField
          onChange={this.handleFieldChange}
          name="description"
          type="text"
          label="description"
          value={this.state.description || ""}
          required
          inputProps={{ pattern: "[A-Za-z \dáÁ_e]{30,2000}"}}         
        />
        <TextField
          onChange={this.handleFieldChange}
          name="duration"
          type="number"
          label="duration"
          value={this.state.duration || ""}
          required
          inputProps={{ min: "1", max:"1000"}}          
        />
        <Button>Criar viagem</Button>
      </ApplicationFormWrapper>
    );
  }
}

export default ApplicationForm;
