import React from "react";
import { connect } from "react-redux";

const daysOfTheWeek = ['segunda', 'terça', 'quarta', 'quinta', 'sexta', 'sábado', 'domingo']


export class Planner extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      newTaskValue: ""
    };
  }


    handleOnChangeNewTodo = event => {
      this.setState({ newTaskValue: event.target.value });
    };

    handleKeyPressNewTodo = event => {
      if (event.key === "Enter") {
        this.handleTodoCreation();
      }
    };

    clearNewTaskValue = () => {
      this.setState({ newTaskValue: "" });
    };

    handleTodoCreation = () => {
      const { newTaskValue } = this.state;
      const { createTodo } = this.props;

      createTodo(newTaskValue);
      this.clearNewTaskValue();
    };

    handleTodoCompletion = todoId => {
      const { completeTodo } = this.props;

      completeTodo(todoId);
    };

    handleTodoDeletion = todoId => {
      const { deleteTodo } = this.props;

      deleteTodo(todoId);
    };





  render() {
    return (
      <div>
        <input/>

        <select>
          <option>Selecione o dia</option>

        </select>

        <button>Adicionar tarefa</button>
      </div>
    )
  }
}

export default connect()(Planner);
